@import "products", "cart", "login";

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Dosis", sans-serif;
}

.navbar {
    color: #000;
    -webkit-box-shadow: 0 10px 6px -8px rgb(90, 90, 90);
    -moz-box-shadow: 0 10px 6px -8px rgb(90, 90, 90);
    box-shadow: 0 10px 6px -8px rgb(167, 161, 161);
}

.category-item {
    display: flex;
